/* Each drawing command simply modifies the software buffer. This is the routine to run to write the data to the buffer.
*
*/
void OLED_display()
{
  int data_count = 0;

  for(int r=0; r < OLED_ROW_COUNT; ++r){
    setXY(r, 0);
    for(int c=0; c < OLED_WIDTH; ++c){
      int idx = r*OLED_WIDTH + c;
      sendByte(buffer[idx]);
    }
  }
}

static void resetDisplay(void)
{
  OLED_displayOff();
  OLED_clear();       // Clear software buffer
  OLED_display();     // Send buffer data to OLED
  OLED_displayOn();
}

void OLED_print(char* string, uint8_t row, uint8_t column) {
	writeStrXY(string, row, column);  
}

void OLED_init()
{
  sendCommand(0xae);		//display off
  sendCommand(0xa6);            //Set Normal Display (default)
  // Adafruit Init sequence for 128x64 OLED module
  sendCommand(0xAE);             //DISPLAYOFF
  sendCommand(0xD5);            //SETDISPLAYCLOCKDIV
  sendCommand(0x80);            // the suggested ratio 0x80
  sendCommand(0xA8);            //SSD1306_SETMULTIPLEX
  sendCommand(0x3F);
  sendCommand(0xD3);            //SETDISPLAYOFFSET
  sendCommand(0x0);             //no offset
  sendCommand(0x40 | 0x0);      //SETSTARTLINE
  sendCommand(0x8D);            //CHARGEPUMP
  sendCommand(0x14);
  sendCommand(0x20);             //MEMORYMODE
  sendCommand(0x00);             //0x0 act like ks0108
  
  //sendcommand(0xA0 | 0x1);      //SEGREMAP   //Rotate screen 180 deg
  sendCommand(0xA0);
  
  //sendcommand(0xC8);            //COMSCANDEC  Rotate screen 180 Deg
  sendCommand(0xC0);
  
  sendCommand(0xDA);            //0xDA
  sendCommand(0x12);           //COMSCANDEC
  sendCommand(0x81);           //SETCONTRAS
  sendCommand(0xCF);           //
  sendCommand(0xd9);          //SETPRECHARGE 
  sendCommand(0xF1); 
  sendCommand(0xDB);        //SETVCOMDETECT                
  sendCommand(0x40);
  sendCommand(0xA4);        //DISPLAYALLON_RESUME        
  sendCommand(0xA6);        //NORMALDISPLAY             
  
  sendCommand(0x2e);            // stop scroll
  //----------------------------REVERSE comments----------------------------//
  sendCommand(0xa0);		//seg re-map 0->127(default)
  sendCommand(0xa1);		//seg re-map 127->0
  sendCommand(0xc8);
  delay(1000);
  //----------------------------REVERSE comments----------------------------//
  // sendcommand(0xa7);  //Set Inverse Display  
  // sendcommand(0xae);		//display off
  sendCommand(0x20);            //Set Memory Addressing Mode
  sendCommand(0x00);            //Set Memory Addressing Mode ab Horizontal addressing mode
  //  sendcommand(0x02);         // Set Memory Addressing Mode ab Page addressing mode(RESET) 
  resetDisplay(); 
}

void OLED_small_eyes(){
  
  OLED_displayOff(); 
  OLED_clear();
  
  // OLED_print("Hello world", 0, 0);
  for(int i = 0; i < OLED_BUFFER_SIZE; i++){
    buffer[i] = epd_bitmap_test[i];
  }

  OLED_display();  
  OLED_displayOn();
}